#pragma config(Motor,  port1,           Left,          tmotorNormal, openLoop)
#pragma config(Motor,  port2,           BackR,         tmotorNormal, openLoop, reversed)
#pragma config(Motor,  port3,           BackL,         tmotorNormal, openLoop)
#pragma config(Motor,  port4,           Right,         tmotorNormal, openLoop, reversed)
